<script>
    document.getElementById("toggle-container").addEventListener("click", function() {
        let balanceDiv = document.getElementById("balance-info");
        let arrow = document.getElementById("arrow-trading-real");

        if (balanceDiv.classList.contains("hidden")) {
            balanceDiv.classList.remove("hidden");
            arrow.classList.add("rotate-180");
        } else {
            balanceDiv.classList.add("hidden");
            arrow.classList.remove("rotate-180");
        }
    });
</script>